function fixedNames = fixCSharpKeywordConflict(names)
%fixCSharpKeywordConflict Checks matlab token name for C# keyword conflicts
%   Checks matlab token names for C# keyword conflicts

%   Copyright 2023 The MathWorks, Inc
    
    % List of C# keywords and a few identifiers that should be avoided
    % Last updated October 2022 from https://learn.microsoft.com/en-us/dotnet/csharp/language-reference/keywords/
    
    keywords = ["abstract","add","and","alias","ascending","args", ...
        "async","await","base","bool","break","by", ...
        "byte","case","catch","char","checked","class","const", ...
        "continue","decimal","default","delegate","descending","do", ...
        "double","else","enum","equals","event", ...
        "explicit","extern","false","file","finally", "for",...
        "foreach","from","get","global","goto","group","if","implicit",...
        "in","init","int","interface","internal","into","is",...
        "join","let","lock","long","managed","nameof","namespace","new",...
        "nint","not","notnull","nuint","null","object","on",...
        "operator","or","orderby","out","override","params",...
        "partial","private","protected","public",...
        "readonly","record","ref","remove","required","return","sbyte",...
        "scoped","sealed","select","set","short","sizeof","stackalloc",...
        "static","string","struct","switch","this","throw","true",...
        "try","typeof","uint","ulong","unchecked","unmanaged",...
        "unsafe","ushort","using","value","var","virtual","void", "volatile",...
        "where","while","with","yield"];
    %add verbatim identifier if there is a keyword conflict
    
    fixedNames = [];

    %use loop to preserve ordering
    for name = names
        if ismember(name, keywords)
            fixedNames = [fixedNames, "@" + name];
        else
            fixedNames = [fixedNames, name];
        end
    end
end

