
function group = getPropertyGroups(obj)
% Provide a custom display to hide Process information if not present

% Copyright 2019 The MathWorks, Inc.

%group = getPropertyGroups@matlab.mixin.CustomDisplay(obj);
if (~(strcmp(obj.ProcessID, "") == 1 || strcmp(obj.ProcessName, "") == 1))
       propList = struct("Version", obj.Version, ...
        "Executable", obj.Executable,...
        "Library", obj.Library,...
        "Home", obj.Home, ...
        "Status", obj.Status, ...
        "ExecutionMode", obj.ExecutionMode,...
        "ProcessID", obj.ProcessID,...
        "ProcessName", obj.ProcessName);
    group = matlab.mixin.util.PropertyGroup(propList);
else
    propList = struct("Version", obj.Version, ...
        "Executable", obj.Executable,...
        "Library", obj.Library,...
        "Home", obj.Home, ...
        "Status", obj.Status, ...
        "ExecutionMode", obj.ExecutionMode);
    group = matlab.mixin.util.PropertyGroup(propList);
end
end
